exports.unpack = function(msg, packager) {
	return {
		data: msg.substring(0, packager.length),
		restData: msg.substring(packager.length)
	};
};

exports.pack = function(row, packager) {
  if(row.length > packager.length) {
    console.log('PACK CUT a: l > pack.l', row, row.length, packager.length, row.substring(0, packager.length));
  }
	return {
		//msg: row
     msg: row.substring(0, packager.length)
	};
};

