exports.unpack = function(msg, packager) {
	return {
		data: msg.substring(0, packager.length),
		restData: msg.substring(packager.length)
	};
};

exports.pack = function(row, packager) {
	if(row.length > packager.length) {
    console.log('PACK CUT an: l > pack.l', row,  row.length, packager.length, row.substring(0, packager.length));
  }
  function pad(num, size){     
    return ('000000000' + num).substr(-size); 
  }
  return {
		//msg: row
    //msg: row.substring(0,packager.length)
    msg: pad(row.substring(0,packager.length), packager.length)
	};
};

