exports.unpack = function(msg, packager) {
	return {
		data: msg.substring(0, packager.length), // get from 0 to end
		restData: msg.substring(packager.length) // remove first n char
	};
};

exports.pack = function(row, packager) {
  if(row.length > packager.length) {
    console.log('PACK CUT n : l > pack.l', row,  row.length, packager.length , row.substring(0,packager.length));
  }
  var intValue;
  function pad(num, size){     
    return ('000000000' + num).substr(-size); 
  }
  try{
    intValue = parseInt(row);  
    return {
		//msg: row
    //msg: intValue.toString().substring(0,packager.length)
      msg: pad(intValue, packager.length)
	  };
  }
  catch (e) {
    console.log('err = ' ,e , '\n Check ', row, 'should be interger');
  }
	
};
